import { createAsyncThunk, createSlice, PayloadAction } from '@reduxjs/toolkit';
import { getWeatherForecast } from '../../utils/api';
import { IForecast } from '../types/data';

interface ForecastState {
  data: IForecast[];
  loading: boolean;
  error: string | null;
}

const initialState: ForecastState = {
  data: [],
  loading: false,
  error: null,
};

export const fetchForecasts = createAsyncThunk<IForecast[], void, { rejectValue: string }>(
  'forecast/fetchForecasts',
  async (_, { rejectWithValue }) => {
    try {
      const response = await getWeatherForecast();
      if (response.success === false) {
        return rejectWithValue(response.message);
      }
      return response.data;
    } catch (err) {
      return rejectWithValue('Failed to fetch forecasts');
    }
  }
);

const forecastSlice = createSlice({
  name: 'forecast',
  initialState,
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(fetchForecasts.pending, (state) => {
        state.loading = true;
        state.error = null;
      })
      .addCase(fetchForecasts.fulfilled, (state, action: PayloadAction<IForecast[]>) => {
        state.loading = false;
        state.data = action.payload;
      })
      .addCase(fetchForecasts.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload || action.error.message || 'Failed to fetch forecasts';
      });
  },
});

export default forecastSlice.reducer;
