import { getWeatherForecast } from '../../utils/api';
import { AppThunk, AppDispatch } from '../types';
import { IForecast } from '../types/data';

export const GET_FORECAST_REQUEST = "GET_FORECAST_REQUEST";
export const GET_FORECAST_SUCCESS = "GET_FORECAST_SUCCESS";
export const GET_FORECAST_FAILED = "GET_FORECAST_FAILED";

export interface IGetForecastRequestAction {
  readonly type: typeof GET_FORECAST_REQUEST;
}

export interface IGetForecastSuccessAction {
  readonly type: typeof GET_FORECAST_SUCCESS;
  readonly items: Array<IForecast>;
}

export interface IGetForecastFailedAction {
  readonly type: typeof GET_FORECAST_FAILED;
  readonly error: string;
}

export type TForecastActions =
  | IGetForecastRequestAction
  | IGetForecastSuccessAction
  | IGetForecastFailedAction;

export const getForecastRequestAction = (): IGetForecastRequestAction => ({
  type: GET_FORECAST_REQUEST,
});

export const getForecastSuccessAction = (items: Array<IForecast>): IGetForecastSuccessAction => ({
  type: GET_FORECAST_SUCCESS,
  items,
});

export const getForecastFailedAction = (error: string): IGetForecastFailedAction => ({
  type: GET_FORECAST_FAILED,
  error,
});

export const getForecasts: AppThunk = () => (dispatch: AppDispatch) => {
  dispatch(getForecastRequestAction());
  getWeatherForecast()
    .then((res: { data: Array<IForecast> }) => {
      dispatch(getForecastSuccessAction(res.data));
    })
    .catch((error: { message: string }) => {
      dispatch(getForecastFailedAction(error.message));
    });
};
