import { IOptions } from '../services/types/data';
import { IForecast, IGender, ILogin, IRegister } from '../services/types/data';

const _url = "https://igorbakhtin1-dostavochkaapi-2cf2.twc1.net";

export const getGender = async (): Promise<Array<IGender>> => {
  return _request(`${_url}/Gender/AllGenders`, {
    method: "GET"
  });
};

export const registerUser = async (data: IRegister): Promise<any> => {
  return _request(`${_url}/User/register`, {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify(data),
  });
};

export const loginUser = async (data: ILogin): Promise<any> => {
  return _request(`${_url}/User/login`, {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify(data),
  });
};

const _request = (url: string, options?: IOptions) => {
  return fetch(url, options)
    .then((res) => _checkResponse(res))
    .then((res) => _checkSuccess(res))
    .catch((err) => {
      console.error(err);
      throw new Error('Network error');
    });
};

const _checkResponse = (res: Response) => {
  return res.ok ? res.json() : res.json().then((err) => Promise.reject(err));
};

const _checkSuccess = (res: any) => {
  if (!res) {
    throw new Error(`Ошибка: ${res}`);
  }
  return res;
};
