import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getForecasts } from '../services/actions/forecast';
import { RootState, AppDispatch } from '../services/types';

const Forecast: React.FC = () => {
  const dispatch = useDispatch<AppDispatch>();
  const { data, loading, error } = useSelector((state: RootState) => state.forecast);

  useEffect(() => {
    debugger
    dispatch(getForecasts() as any);
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div>
      <h1>Weather Forecast</h1>
      <ul>
        {data.map((forecast) => (
          <li key={forecast.date}>
            {forecast.date}: {forecast.temperatureC}°C / {forecast.temperatureF}°F - {forecast.summary}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Forecast;
