import {
    GET_FORECAST_REQUEST,
    GET_FORECAST_SUCCESS,
    GET_FORECAST_FAILED,
    TForecastActions,
  } from '../actions/forecast';
  import { IForecast } from '../types/data';
  
  interface IForecastState {
    data: IForecast[];
    loading: boolean;
    error: string | null;
  }
  
  const initialState: IForecastState = {
    data: [],
    loading: false,
    error: null,
  };
  
  export const forecastReducer = (state = initialState, action: TForecastActions): IForecastState => {
    switch (action.type) {
      case GET_FORECAST_REQUEST:
        return {
          ...state,
          loading: true,
          error: null,
        };
      case GET_FORECAST_SUCCESS:
        return {
          ...state,
          loading: false,
          data: action.items,
        };
      case GET_FORECAST_FAILED:
        return {
          ...state,
          loading: false,
          error: action.error,
        };
      default:
        return state;
    }
  };
